// Prepare data for Gemini API
const binaryData = items[0].binary.file.data;
const assignmentType = items[0].json.assignmentType;
const userEmail = items[0].json.userEmail;
const fileName = items[0].json.filename;
const mimeType = items[0].json.mimeType;

const base64Data = binaryData;

return [{
  json: {
    file: base64Data,
    filename: fileName,
    assignmentType: assignmentType,
    userEmail: userEmail,
    mimeType: mimeType
  },
  binary: {
    file: items[0].binary.file
  }
}];

// Extract the actual HTML from Gemini response
let htmlContent = '';

// --- Existing logic to find the text response ---
if (
  items[0].json.content &&
  items[0].json.content.parts &&
  items[0].json.content.parts.length > 0 &&
  items[0].json.content.parts[0].text
) {
  htmlContent = items[0].json.content.parts[0].text;
} else if (
  items[0].json.candidates &&
  items[0].json.candidates[0].content &&
  items[0].json.candidates[0].content.parts[0].text
) {
  htmlContent = items[0].json.candidates[0].content.parts[0].text;
} else {
  // Fallback for debugging if structure is unexpected
  htmlContent = JSON.stringify(items[0].json, null, 2);
}


// --- NEW CODE TO CLEAN THE RESPONSE ---
// This regular expression finds and removes the starting ```html and the closing ```.
// It also handles surrounding whitespace and newlines, making it robust.
const cleanedHtml = htmlContent.replace(/^`{3}(?:html)?\s*|\s*`{3}$/g, '').trim();


// --- Return the cleaned data ---
return [
  {
    json: {
      html: cleanedHtml, // Use the cleaned variable here
      filename: items[0].json.filename || 'document',
      assignmentType: items[0].json.assignmentType || 'Assignment',
      userEmail: items[0].json.userEmail || ''
    }
  }
];
